import React, {Component} from 'react'

export class Text extends Component {
    constructor(props) {
        super(props);
        const token = JSON.parse(localStorage.getItem('userData')).token
        const id = this.props.item._id
        const value = this.props.item.value
        const owner = this.props.item.owner
        console.log(props.item._id)
        this.state = {
            id,
            value,
            owner,
            token
        }
        console.log(this.value)
    }

    componentDidMount() {

    }

    changeHandler = (e) => {
        const target = e.target
        const val = target.value
        const name = target.name

        this.setState({
            [name]:val
        })
    }

    request = async (url,method = 'GET',body = {},headers = {}) => {
        try {
            if(body) {
                body = JSON.stringify(body)
                headers['Content-Type'] = 'application/json'
            }
            const response = await fetch(url, {method,body,headers})

            if(!response.ok) {
                throw new Error(data.message || 'Request throwing is wrong!')
            }

            return data
        } catch (e) {
            throw e
        }
    }

    async editHandler(e) {
        try {
            await this.request(`api/text/edit/${this.state.id}`, 'PUT', {value: `${this.state.value}`}, {Authorization: `Bearer ${this.state.token}`})

        } catch (e) {
        }
    }
    async deleteHandler(e) {
        try {
            await this.request(`api/text/delete/${this.state.id}`, 'DELETE', null, {Authorization: `Bearer ${this.state.token}`})
        } catch (e) {
        }
    }


    render() {
        return (
            <tr>
                <td>No</td>
                <td>
                    <input
                        type="text"
                        name="value"
                        value={this.state.value}
                        onChange={this.changeHandler}
                    />
                </td>
                <td>
                    <button
                        className="btn btn-small green darken-1"
                        style={{marginRight:"1em",marginLeft:"1em"}}
                        onClick={this.editHandler}
                    >Edit</button>
                    <button
                        className="btn btn-small red darken-2"
                        onClick={this.deleteHandler}
                    >Delete</button>
                </td>
            </tr>
        )
    }

}
