import {React,useCallback,useState,useEffect,useContext} from 'react'
import {AuthContext} from "../context/AuthContext";
import {useHttp} from "../hooks/http.hook";
import {Loader} from "./components/Loader";
import {Text} from "./components/Text";


export const TextList = () => {
    const {token} = useContext(AuthContext)
    //const [id,setId] = useState(null)
    const [texts,setTexts] = useState({})
    const [newText,setNewText] = useState({})
    const {} = useState({})
    const [update,setUpdate] = useState(false)
    const {request,loading} = useHttp()

    const fetchTexts = useCallback( async () => {
        try {
            let fetched = await request('/api/text','GET',null,{
                Authorization: `Bearer ${token}`
            })
            console.log(fetched)
            setTexts(fetched)
        } catch (e) { }
    },[request,token])

    useEffect(() => {
        window.M.updateTextFields()
    },[])

    useEffect(() => {
        fetchTexts()
    },[fetchTexts,update])


    const changeHandler = (event) => {
        let value = event.target.value
        setNewText({
            ...newText,
            [event.target.name]: value
        })
        console.log(newText)
    }



    const editHandler = useCallback( async  (id) => {
        try {
            console.log(newText)
            await request(`api/text/edit/${id}`,'PUT',{value: `${newText[id]}`},{Authorization: `Bearer ${token}`})
        } catch (e) { }
        setUpdate(!update)
    },[request,token,update])

    if(loading) {
        return <Loader />
    }

    if(!texts.length) {
        return <h5 className="center">No Texts. Create one!</h5>
    }

    return(
        <table>
            <thead>
            <tr>
                <th>â„–</th>
                <th>Text</th>
                <th>Action</th>
            </tr>
            </thead>
            <tbody>
            {texts.map((text,i) => {
                return (
                    <tr>
                        <Text  />
                    </tr>
                )
            })}
            </tbody>
        </table>
    )
}
