import React,{useState,useContext,useEffect} from "react"
import {AuthContext} from "../../context/AuthContext"
import {useHttp} from "../../hooks/http.hook"

export const Text1 = ({text,i}) => {
    const {token} = useContext(AuthContext)
    const {request} = useHttp()
    const id = text['_id']
    const value = text['value']
    const owner = text['owner']
    const ie = i
    const {form,setForm} = useState({
        i:{ie},
        id,
        value,
        owner,
    })

    const changeHandler = (e) => {
        const target = e.target
        const val = target.value
        const name = target.name

        setForm({
            [name]:val
        })
    }

    const editHandler = async () => {
        try {
            const data = await request(`api/text/edit/${form['id']}`, 'PUT', {value: `${form['value']}`}, {Authorization: `Bearer ${token}`})
            console.log('Editing!')
        } catch (e) {
            console.log(e.message)
        }
    }

    const deleteHandler = async () => {
        try {
            const data = await request(`api/text/delete/${form['id']}`, 'DELETE', null, {Authorization: `Bearer ${token}`})
            console.log('Deleting!')
        } catch (e) {
            console.log(e.message)
        }
    }

    return (
        <tr>
            <td>{form['i'] + 1}</td>
            <td>
                <input
                    type="text"
                    name="value"
                    value={form['value']}
                    onChange={changeHandler}
                />
            </td>
            <td>
                <button
                    className="btn btn-small green darken-1"
                    style={{marginRight:"1em",marginLeft:"1em"}}
                    onClick={editHandler}
                >Edit</button>
                <button
                    className="btn btn-small red darken-2"
                    onClick={deleteHandler}
                >Delete</button>
            </td>
        </tr>
    )

}
