import {React,useCallback,useState,useEffect,useContext} from 'react'
import {AuthContext} from "../../context/AuthContext";
import {useHttp} from "../../hooks/http.hook";
import {Loader} from "./Loader";


export const TextList = () => {
    const {token} = useContext(AuthContext)
    //const [id,setId] = useState(null)
    const [texts,setTexts] = useState({})
    const [update,setUpdate] = useState(1)
    const {request,loading} = useHttp()

    const fetchTexts = useCallback( async () => {
        try {
            let fetched = await request('/api/text','GET',null,{
                Authorization: `Bearer ${token}`
            })
            console.log(fetched)
            setTexts(fetched)
        } catch (e) { }
    },[request,token,texts])

    useEffect(() => {
        window.M.updateTextFields()
    },[])

    useEffect(() => {
        fetchTexts()
    },[fetchTexts,update])

    const deleteHandler = useCallback(async (id) => {
        try {
            let data = await request(`api/text/delete/${id}`,'DELETE',null,{Authorization: `Bearer ${token}`})

        } catch (e) { }
        setUpdate(Math.abs(update-3))
    },[request,token,update])

    const editHandler = useCallback( async  (id) => {
        try {
            await request(`api/text/edit/${id}`,'PUT',{value: ''},{Authorization: `Bearer ${token}`})
        } catch (e) { }
        setUpdate(Math.abs(update-3))
    },[request,token,update])

    if(loading) {
        return <Loader />
    }

    if(!texts.length) {
        return <h5 className="center">No Texts. Create one!</h5>
    }

    return(
        <table>
            <thead>
            <tr>
                <th>№</th>
                <th>Text</th>
                <th>Action</th>
            </tr>
            </thead>
            <tbody>
            {texts.map((text,i) => {
                return (
                    <tr key={text._id}>
                        <td>{i+1}</td>
                        <td><input
                            type="text"
                            value={text._id}
                            hidden
                        />
                            <input
                                type="text"
                                value={text.value}
                                onChange={event => {}}
                                defaultValue={text.value}
                            /></td>
                        <td>
                            <button
                                className="btn btn-small green darken-1"
                                style={{marginRight:"1em",marginLeft:"1em"}}
                                onClick={() => editHandler(text._id)}
                            >Edit</button>
                            <button
                                className="btn btn-small red darken-2"
                                onClick={() => deleteHandler(text._id)}
                            >Delete</button>
                        </td>
                    </tr>
                )
            })}
            </tbody>
        </table>
    )
}
